};

const Reducer = (state= initialState, action ) => {
    const newState = {...state};
    switch (action.type) {
        case 'QUEST_NEXT_ASYNC' :
            console.log("ACTION", action.value)
            console.log("CINDEX", newState.cIndex)
            newState.cIndex = newState.cIndex + action.value;
            break;
        case 'QUEST_NEXT':
            newState.cIndex = newState.cIndex + action.value;
            break;
    }
    
}

