import React, { Component, useEffect } from 'react'
import { connect } from 'react-redux'
import  { qBank }  from './QuestCollection'
import '../style.css'

class Quiz extends Component {

    constructor(props) {
        super(props);
        this.onquestNext = this.onquestNext.bind(this);
    
        this.state = {
             options: [],
             Answer: null,
             //cIndex : 0,
             disabled: true,
             qFinish: false,
             score: 0
        }
    }


    getQuestion = () => {
        //const{cIndex} = this.state;
        this.setState(() => {
            return{
                question: qBank[this.props.cIndex].question,
                options: qBank[this.props.cIndex].options,
                answer: qBank[this.props.cIndex].answer  
            }
        })
    }


    componentDidMount(){
        this.getQuestion();
    }

    onquestNext = () => {
        const { Answer, score, answer, cIndex } = this.state

        if(Answer === answer){
            this.setState({
                score: score + 1
            })
        }

        this.setState({
            cIndex: cIndex + 1,
            Answer: null
        })
    }

    submitHandler = () => {
        if(this.props.cIndex === qBank.length - 1){
            this.setState({
                qFinish: true
            })
        }
    }

    checkAnswer = answer => {
        this.setState({
            Answer: this.state.answer,
            disabled: false
        })
    }


    componentDidUpdate = (prevProps, prevState) => {
        //const{cIndex} = this.state;
        const{cIndex} = this.props.cIndex;
        console.log("NEWVALUE",this.props.cIndex)
        console.log("componentdidupdate")
        console.log({cIndex})
        if(prevProps.cIndex !== prevState.cIndex){
           this.setState((prevProps) => {
             return{
                question: qBank[prevProps.cIndex].question,
                options: qBank[prevProps.cIndex].options,
                answer: qBank[prevProps.cIndex].answer
              }
            })
        }
    }

    // componentDidUpdate = () => {
    //     if(prevProps)
    //     this.setState(() => {
    //         return{
    //             question: qBank[this.props.cIndex].question,
    //             options: qBank[this.props.cIndex].options,
    //             answer: qBank[this.props.cIndex].answer
    //         }
    //     })
    // }


    render() {
        const{ question, options, Answer, qFinish } = this.state
        
        if(qFinish) {
         return (
             <div>
                 <h2>Your Score is {this.state.score} out of {qBank.length} </h2>
             </div>
          )
         }
         
        return (
            <div>
                {console.log("this.props", this.props)}
                {console.log("Index", this.props.cIndex)}
                <h2>{question}</h2>
                <span>{`Question ${this.props.cIndex + 1} of ${qBank.length} `}</span>
                {
                    options.map((option, index ) => 
                        <p key ={option.id} className={`options ${Answer === option? "selected" : null}`}
                            onClick = {() => this.checkAnswer(option)}>
                                {option}
                        </p>
                        )
                }
                {this.props.cIndex  < qBank.length - 1 && 
                <button disabled = {this.state.disabled} onClick={() => {this.props.onquestNext()}}>
                    Next </button>}
                {this.props.cIndex === qBank.length -1 &&
                <button onClick={this.state.submitHandler} disabled ={this.state.disabled} >
                    Submit
                </button> }
            </div>
        )
        
    }
}
 

const mapStateToProps = state => {
    return{
    cIndex: state.cIndex
    }
}

const mapDispatchToProps = dispatch => {
    return{
    onquestNext: () => dispatch({type: 'QUEST_NEXT_ASYNC', value: 1}), //{type: 'QUEST_NEXT_ASYNC', value: 1}
    
    }
}



export default connect(mapStateToProps, mapDispatchToProps)(Quiz)


