import { QUEST_NEXT_ASYNC } from '../constants/quiz'
//export const QUESTION_NEXT = 'QUESTION_NEXT';

const initialState = {
    cIndex: 0,
    Answer: 0,
    score: 0
};

console.log(initialState)

const Reducer = (state = initialState, action) => {
   // const newState = {...state};
    //console.log(newState);
    console.log("action" , action.value)

    switch (action.type) {
        case QUEST_NEXT_ASYNC: 
            return {
                Answer: initialState.Answer + action.value,
                score: initialState.score + action.value,
                cIndex: initialState.cIndex + action.value
            }  
        
        break;
        default: return state;
    }
    
};

export default Reducer
