//import { QUEST_NEXT_ASYNC } from '../constants/quiz'
//export const QUESTION_NEXT = 'QUESTION_NEXT';
import { QUEST_NEXT_ASYNC } from '..sagas/saga'

const initialState = {
    cIndex: 0
};

console.log("Initialstate", initialState)

const Reducer = (state = initialState, action) => {
   // const newState = {...state};
    //console.log("action" , action.value)
    switch (action.type) {
        case QUEST_NEXT_ASYNC:
            console.log("Action" , action.value)
            console.log("INDEX", initialState.cIndex)
            return {
                cIndex: initialState.cIndex + action.value  
            } 
        default: return state;
    }
    
};


export default Reducer
