import React, { Component } from 'react'
import  { qBank }  from './QuestCollection'
import '../style.css'

class Quiz extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             options: [],
             Answer: null,
             cIndex : 0,
             disabled: true,
             qFinish: false,
             score: 0
        }
    }

    loadQuiz = () => {
        const{cIndex} = this.state;
        this.setState(() => {
            return{
                question: qBank[cIndex].question,
                options: qBank[cIndex].options,
                answer: qBank[cIndex].answer
            }
        })
    }

    nextQuestionHandler = () => {
        const [ Answer, answer, score ] = this.state

        if(Answer === answer){
            this.setState({
                score: score + 1
            })
        }

        this.setState({
            cIndex: this.state.cIndex + 1,
            Answer: null
        })
    }

    finishHandler = () => {
        if(this.state.cIndex === qBank.length -1){
            this.setState({
                qFinish: true
            })
        }
    }

    componentDidMount(){
        this.loadQuiz();
    }

    checkAnswer = answer => {
        this.setState({
            Answer: answer,
            disabled: false
        })
    }

    componentDidUpdate(prevProps, prevState){
        const{cIndex} = this.state;
        if(this.state.cIndex !== prevState.cIndex){
           this.setState(() => {
             return{
                question: qBank[cIndex].question,
                options: qBank[cIndex].options,
                answer: qBank[cIndex].answer
              }
            })
        }
    }
    

    // state = {
    //     questionBank: []
    // };

    // getQuestions = () => {
    //     QuestCollection().then(question => {
    //         this.setState({
    //             questionBank: question
    //         });
    //     });

    // };

    // componentDidMount() {
    //     this.getQuestions();
    // }
    render() {
        const{ question, options, cIndex, Answer, qFinish } = this.state
        return (
            <div>
                <h2>{question}</h2>
                <span>{`Question ${cIndex + 1} of ${qBank.length} `}</span>
                {
                    options.map(option => 
                        <p key ={option.id} className={`options ${Answer === option? "selected" : null}`}
                            onClick = {() => this.checkAnswer(option)}>
                                {option}
                        </p>
                        )
                }
                {cIndex < qBank.length - 1 && 
                <button disabled = {this.state.disabled} onClick={this.nextQuestionHandler}>
                    Next Question</button>}
                {currentIndex === qBank.length -1 &&
                <button onClick={this.finishHandler} disabled ={this.state.disabled} >
                    Finish
                </button> }
            </div>
        )
    }
}

export default Quiz

