export const QUESTION_NEXT = 'QUESTION_NEXT';

const initialState = {
    cIndex: 0,
    Answer: null,
    score: 0
};

console.log(cIndex)

const Reducer = (state = initialState, action) => {
    const newState = {...state};

    switch (action.type) {
        case QUESTION_NEXT: {
                newState.Answer += action.value;
                newState.score += action.value;
                newState.cIndex += action.value  
        }
        break;
        default: return state;
    }
    
};

export default Reducer
