import React, { Component } from 'react'
import { connect } from 'react-redux'
import  { qBank }  from './QuestCollection'
import '../style.css'

class Quiz extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             options: [],
             //Answer: null,
             //cIndex : 0,
             disabled: true,
             qFinish: false,
            // score: 0
        }
    }

    getQuestion = () => {
        //const{cIndex} = this.state;
        this.setState(() => {
            return{
                question: qBank[this.props.cIndex].question,
                options: qBank[this.props.cIndex].options,
                answer: qBank[this.props.cIndex].answer
            }
            
        })

    }
    

    componentDidMount(){
        this.getQuestion();
    }

    nextHandler = () => {
        const { answer } = this.state

        if(this.props.Answer === answer){
            this.setState({
                score: this.props.score + 1
            })
        }

        this.setState({
            cIndex: this.props.cIndex + 1,
            Answer: null
        })
    }

    submitHandler = () => {
        if(this.state.cIndex === qBank.length - 1){
            this.setState({
                qFinish: true
            })
        }
    }

    checkAnswer = answer => {
        this.setState({
            Answer: this.props.answer,
            disabled: false
        })
    }

    componentDidUpdate(prevProps, prevState){
        //const{cIndex} = this.state;
        if(this.props.cIndex !== prevState.cIndex){
           this.setState(() => {
             return{
                question: qBank[this.props.cIndex].question,
                options: qBank[this.props.cIndex].options,
                answer: qBank[this.props.cIndex].answer
              }
            })
        }
    }
    
    //  useEffect(() => {
    //      getQuestion();
    //  },[])
    // state = {
    //     questionBank: []
    // };

    // getQuestions = () => {
    //     QuestCollection().then(question => {
    //         this.setState({
    //             questionBank: question
    //         });
    //     });

    // };

    // componentDidMount() {
    //     this.getQuestions();
    // }
    render() {
        const{ question, options, cIndex, Answer, qFinish } = this.state
        
        if(qFinish) {
         return (
             <div>
                 <h2>Your Score is {this.props.state.score} out of 12</h2>
             </div>
          )
         }
         
        return (
            <div>
                <h2>{question}</h2>
                <span>{`Question ${this.props.cIndex + 1} of ${qBank.length} `}</span>
                {
                    options.map(option => 
                        <p key ={option.id} className={`options ${Answer === option? "selected" : null}`}
                            onClick = {() => this.checkAnswer(option)}>
                                {option}
                        </p>
                        )
                }
                {this.props.cIndex < qBank.length - 1 && 
                <button disabled = {this.state.disabled} onClick={()=> {this.props.onquestNext()}}>
                    Next </button>}
                {this.props.cIndex === qBank.length -1 &&
                <button onClick={this.submitHandler} disabled ={this.state.disabled} >
                    Submit
                </button> }
            </div>
        )
        
    }
}


const mapStateToProps = state => {
    return{
    Answer: state.Answer,
    score: state.score,
    cIndex: state.cIndex

    }
}
//console.log(this.props.Answer)
//console.log(question)

const mapDispatchToProps = dispatch => {
    return{
    onquestNext: () => dispatch({type: 'QUEST_NEXT', value: 1})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Quiz)


